    public static String getCity(String province) {
        if (province == null) {
            String[] arr = place[random.nextInt(place.length)];
            return arr[random.nextInt(arr.length - 1) + 1];
        }
        for (String[] pro : place) {
            if (province.equals(pro[0])) {
                return pro[random.nextInt(pro.length - 1) + 1];
            }
        }
        return null;
    }
